package com.microservices.catalog;

import java.math.BigDecimal;

import lombok.Data;
import lombok.Builder;
import lombok.NoArgsConstructor;
import lombok.AllArgsConstructor;

/**
 * DTO que representa la vista pública de un producto que se expone al cliente.
 *
 * Contiene únicamente los campos necesarios para la API REST (identificador,
 * nombre, descripción, precio, stock, categoría, URL de imagen y estado activo).
 * Se añaden constructores y builder para facilitar la compatibilidad con MapStruct
 * y frameworks de serialización.
 */
@Data
@Builder
@NoArgsConstructor
@AllArgsConstructor
public class ProductDTO {

    /** Identificador del producto */
    private Long id;

    /** Nombre del producto */
    private String name;

    /** Descripción del producto */
    private String description;

    /** Precio del producto */
    private BigDecimal price;

    /** Stock disponible */
    private Integer stock;

    /** Categoría a la que pertenece */
    private String category;

    /** URL de la imagen del producto */
    private String imageUrl;

    /** Indica si el producto está activo (visible) */
    private boolean active;

}
